import React from 'react';
import Carousel from 'react-bootstrap/Carousel';

import logo from '../assets/logo1.png'

const Optowelt = () => {
    return (
        <div>
        <Carousel className=" text-center">
            <img className="d-block w-100%"
                        width="1400"
                        height="570"
                        alt="optowelt" 
                        src={logo}
                        />
                         <Carousel.Caption style={{ color: 'red' }}>
                        <h1>Более 100 стран</h1>
                        <p>Отправляйте грузы по всему миру</p>
                    </Carousel.Caption>
                        </Carousel>
           <h3>СООБЩИТЕ ПРОИЗВОДИТЕЛЯМ ТОВАРОВ,<br/>ЧТО ВАМ НУЖНО</h3>
           <div>
           <h5>Мы разошлем вашу заявку по базе поставщиков, они сами свяжутся с вами.</h5>
           </div>
        </div>
    );
};

export default Optowelt;
