import React from 'react';
import Carousel from 'react-bootstrap/Carousel';
import logo from '../assets/logo2.png'

const Optowelt = () => {
    return (
        <div>
        <Carousel xs={12} sm={6} md={4} className=" text-center">
            <img xs={12} sm={6} md={4} className="d-block w-100%"
                        width="1400"
                        height="570"
                        alt="optowelt" 
                        src={logo}
                        />
                         <Carousel.Caption style={{ color: 'red' }}>
                         <h1 >Более 5 000000 производителей товаров</h1>
                        <h5 >Оставьте заявку и покупатели сами напишут вам</h5>
                    </Carousel.Caption>
                        </Carousel>
           <h3>СООБЩИТЕ ПРОИЗВОДИТЕЛЯМ ТОВАРОВ,<br/>ЧТО ВАМ НУЖНО</h3>
           <div>
           <h5>Мы разошлем вашу заявку по базе поставщиков, они сами свяжутся с вами.</h5>
           </div>
        </div>
    );
};

export default Optowelt;
