import React from 'react';
import Carousel from 'react-bootstrap/Carousel';
import logo from '../assets/logo9.png'
import { Container, Row, Col, Card, Button } from 'react-bootstrap';


const Optowelt = () => {
    return (
        <Col className="center"  xs={12} sm={6} md={4} >
        <Card  style={{ width: '100%' }}>
            <Card.Img  variant="top" style={{ width: '100%', height: '100%' }} src={logo} />
            <Card.Body>
                <Card.Title className="text-center" > <h2>СООБЩИТЕ ПОСТАВЩИКАМ, ЧТО ВАМ НУЖНО.</h2> 
                Мы разошлем вашу заявку по большой базе поставщиков, они сами свяжутся с вами</Card.Title>
                <Card.Text >
                Вы хотите приобрести товар оптом? Создайте заявку, и команда Qoovee уведомит компании по выбранной категории о вашем запросе. Из предложенных откликов вы сможете выбрать наиболее подходящий вам по ценам и условиям.
                </Card.Text>
                
            </Card.Body>
        </Card>
    </Col>
    );
}

export default Optowelt;
